# k/n = number
# k - int
# n - int
# s - string

samples = [
    (
        3452,
        "",
        [
            "KYQTWXDLINFBHRGZVCUSAMOEPJ",
            "YUGTZIWNVSALBXOCDMPFEKJRQH",
        ],
    )
]
# %%


def merge_the_tools(input_string, k):
    # your code goes here
    si_n = int(len(input_string) / k)
    rslt = []
    for i in range(si_n):
        si = i * k
        ti = input_string[si : si + k]
        already_printed = set()
        ui = []
        for c in ti:
            if c in already_printed:
                continue
            already_printed.add(c)
            ui.append(c)
        rslt.append("".join(ui))
    return rslt


def show_sample(idx):
    k = samples[0][0]
    sample = samples[idx][1]
    y_true = samples[idx][2]
    y_pred = merge_the_tools(sample, k)
    print(k)
    print(sample)
    print(y_true)
    print(y_pred)
    print(all([y_true[i] == y_pred[i] for i in range(len(y_true))]))


show_sample(0)
